import  React, { useState } from "react";
import { makeStyles } from '@material-ui/core/styles';
import MenuSlider from '@material-ui/core/Drawer'; 
import {
  AppBar,
  Toolbar,
  ListItem,
  IconButton,
  ListItemText,
  Avatar,
  Divider,
  List,
  Typography,
  Box,
  Backdrop,
  ListItemIcon,
} from "@material-ui/core";
import {
  Menu,
  AssignmentInd,
  Home,
  Apps,
  ContactMail,
} from "@material-ui/icons";
import avatar from "../../assets/images/avatar.png"

// CSS styles
const useStyles = makeStyles(theme =>({
  menuSliderContainer: {
    width: 250,
    background: "#511",
    height: "100%"
  },
  avatar: {
    display: "block",
    margin: "0.5rem auto",
    width: theme.spacing(13),
    height: theme.spacing(13)
  },
  menuItem: {
    color: "tan"
  }
}));
//

// Object to map through for menu icons and text
const menuItems = [
  {
    listIcon: <Home />,
    listText: "Home"
  },
  {
    listIcon: <AssignmentInd />,
    listText: "Resume"
  },
  {
    listIcon: <Apps />,
    listText: "Portfolio"
  },
  {
    listIcon: <ContactMail />,
    listText: "Contact Me"
  }
]

const Navbar = () => {
  const [state, setState] = useState({
    right: false
  });

  const toggleSlider = (slider, open) => () => {
    setState({...state, [slider]: open })
  };

  const classes = useStyles();

  const sideList = (slider) => {
    <Box className={classes.menuSliderContainer} component="div">
        <Avatar className={classes.avatar} src={avatar} alt="Josh Padilla" />
        <Divider />
        <List>

          {menuItems.map((menuItem, key) =>(
            <ListItem button key={key}>
            <ListItemIcon className={classes.menuItem}>
              {menuItem.listIcon}
            </ListItemIcon>
            <ListItemText className={classes.menuItem} primary={menuItem.listText}>

            </ListItemText>
          </ListItem>
          ))}

        </List>
      </Box>
  }

  return (
      <>
      
    <Box component="nav">
      <AppBar position="static" style={{background: "#222"}}>
        <Toolbar>
          <IconButton onClick={toggleSlider("right", true)}>
            <Menu style={{ color: "tomato" }}/>
          </IconButton>
          <Typography variant="h5" style={{ color: "tan" }}>
           My Portfolio
          </Typography>
          <MenuSlider open={state.right}>
            {sideList("right")}
          </MenuSlider>
        </Toolbar>
      </AppBar>
    </Box>
    </>
  );
};

export default Navbar;
